# Register PSRepository
Register-PackageSource -ProviderName 'PowerShellGet' -Name:('JumpCloudPowershell-Dev') -Location:('https://pkgs.dev.azure.com/JumpCloudPowershell/_packaging/Dev/nuget/v2') -Credential:(New-Object System.Management.Automation.PSCredential($env:SYSTEM_ACCESSTOKEN, ($env:SYSTEM_ACCESSTOKEN | ConvertTo-SecureString -AsPlainText -Force)))